import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { logoutUser } from '../../actions/authActions';
import { clearCurrentProfile } from '../../actions/profileActions';

import logo from '../../img/logo.png';

class Navbar extends Component {
    onLogoutClick = e => {
        e.preventDefault();
        this.props.clearCurrentProfile();
        this.props.logoutUser();
        // Cannot use history here, bc this is not a Route
        window.location.href = '/';
    };

    render() {
        const { isAuthenticated, user } = this.props.auth;

        const authLinks = (
            <ul className="navbar-nav ml-auto">
                <li className="nav-item">
                    <Link className="nav-link" to="/feed">
                        Post Feed
                    </Link>
                </li>
                <li className="nav-item">
                    <Link className="nav-link" to="/dashboard">
                        Dashboard
                    </Link>
                </li>
                <li className="nav-item">
                    <Link
                        onClick={this.onLogoutClick}
                        className="btn btn-outline-danger"
                        to="/dashboard"
                    >
                        <img
                            src={user.avatar}
                            alt={user.name}
                            className="rounded-circle"
                            title="You must have a Gravatar connected to your email to display an image"
                            style={{ width: '25px', marginRight: '5px' }}
                        />
                        Sign Out
                    </Link>
                </li>
            </ul>
        );

        const guestLinks = (
            <ul className="navbar-nav ml-auto">
                <li className="nav-item px-1">
                    <Link className="btn btn-outline-danger my-2 my-sm-0" to="/register">
                        Sign Up
                    </Link>
                </li>
                <li className="nav-item px-1">
                    <Link className="btn btn-outline-warning my-2 my-sm-0" to="/login">
                        Sign In
                    </Link>
                </li>
            </ul>
        );

        // Return is only for JSX
        return (
            <nav className="navbar navbar-expand-sm navbar-dark bg-dark mb-4 sticky-top">
                <div className="container">
                    <Link className="navbar-brand" to="/">
                        <img src={logo}/>
                    </Link>
                    <button
                        className="navbar-toggler"
                        type="button"
                        data-toggle="collapse"
                        data-target="#mobile-nav"
                    >
                        <span className="navbar-toggler-icon" />
                    </button>

                    <div className="collapse navbar-collapse" id="mobile-nav">
                        <ul className="navbar-nav mr-auto">
                            <li className="nav-item">
                                <Link className="nav-link" to="/profiles">
                                    {' '}
                                    Developers
                                </Link>
                            </li>
                        </ul>
                        {isAuthenticated ? authLinks : guestLinks}
                    </div>
                </div>
            </nav>
        );
    }
}

Navbar.propTypes = {
    logoutUser: PropTypes.func.isRequired,
    auth: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
    auth: state.auth
});

export default connect(
    mapStateToProps,
    { logoutUser, clearCurrentProfile }
)(Navbar);
